import React, { useState } from 'react'
import "./Today.css"
import { useSelector } from 'react-redux'


function Today() {

    const {showTodayInMiddeSreen} = useSelector(state => state.uiSlice)
    const [mouseOver,setMouseOver] = useState(false)
    const [showDoneTasks,setShowDoneTasks] = useState(false)

    return (
        <div onMouseOver={()=>setMouseOver(true)} onMouseLeave={()=>setMouseOver(false)} className='Today' style={{ 
                                        transform : showTodayInMiddeSreen ? "translateX(-24%)" : "translateX(0%)",
                                        width : showTodayInMiddeSreen ? "36%": "30%",
                                        scale : mouseOver ? "1" : "0.9"
                                        
                                        }}>
            <div>
                <h2>Today's Tasks</h2>
                
            </div>

            <button className='flip-page' onClick={()=>setShowDoneTasks(!showDoneTasks)}>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                <path stroke-linecap="round" stroke-linejoin="round" d="M7.5 21L3 16.5m0 0L7.5 12M3 16.5h13.5m0-13.5L21 7.5m0 0L16.5 12M21 7.5H7.5" />
                </svg>
            </button>

            <div className='tasks-done' style={{transform: showDoneTasks ? "translateX(0%)" :"translateX(112%)"}}>
                
            </div>

        </div>
    )
}

export default Today
