import { createSlice } from '@reduxjs/toolkit'



const date = new Date()
const today = date.getDate()
const month = date.getMonth()
const year = date.getFullYear()



function getDatesArray(month,year){

    //last date of current month
    const d = new Date(year, month + 1, 0);
    const lastDate = d.getDate();

    const dates = []

    const firstDate = new Date(year,month,1)
    const first_Day = firstDate.getDay()

    let counter = -1 * first_Day

    for(counter;counter < lastDate;counter++){
        if(counter < 0){
            dates.push(0)
        }
        else{
            dates.push(counter+1)
        }
    }

    return dates

}






const initialState = {
    today : {
        today,
        month,
        year
    },
    dates : getDatesArray(month,year),
    currMonth: month,
    currYear: year,
}



export const uiSlice = createSlice({
  name: 'uiSlice',
  initialState,
  reducers: {
    increment: (state) => {},
  },
})

// Action creators are generated for each case reducer function
export const { increment } = uiSlice.actions

export default uiSlice.reducer
