import React, { useState, Fragment, useEffect } from 'react'
import './Calendar.css'


function getDatesArray(month,year){

    //last date of current month
    const d = new Date(year, month + 1, 0);
    const lastDate = d.getDate();

    const dates = []

    const firstDate = new Date(year,month,1)
    const first_Day = firstDate.getDay()

    let counter = -1 * first_Day

    for(counter;counter < lastDate;counter++){
        if(counter < 0){
            dates.push(<button key={counter} className='no-date'>null</button>)
        }
        else{
            dates.push(<button key={counter} className='no-click'>{counter+1}</button>)
        }
    }

    return dates

}


function Calendar() {

    //current


    const [dates,setDates] = useState()
    const days = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"]


    const nextMonth = () => {
        const newDatesArray = getDatesArray(month+1,year)
        setDates(newDatesArray)
    }


    console.log(month)


    
    return (
        <Fragment>
            <div className='Calendar-container'>
                <button className='prev-month'>
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1" stroke="white" class="w-6 h-6">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M15.75 19.5L8.25 12l7.5-7.5" />
                    </svg>
                </button>
                {
                    days.map((day)=>{
                        return <h4 key={day} className="days">{day}</h4>
                    })
                }
                {  
                    dates
                }
                <button onClick={nextMonth} className='next-month'>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1" stroke="white" class="w-6 h-6">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M8.25 4.5l7.5 7.5-7.5 7.5" />
                        </svg>
                </button>
            </div>                
        </Fragment>
    )
}

export default Calendar
