import React, { useEffect, useState } from 'react'
import "./Today.css"
import history from  '../../Assets/history.png'
import {useDispatch, useSelector} from 'react-redux'
import { addTask, doneTask } from '../../Redux/userSlice'


const color = [  {back:"#e8deee",color:"#412454"},  
                 {back:"#eee0da",color:"#442a1e"},  
                 {back:"#d3e5ef",color:"#183347"},  
                 {back:"#fdecc8",color:"#402c1b"},  
                 {back:"#ffe2dd",color:"#5d1715"},  
                 {back:"#dbeddb",color:"#1c3829"},  
                 {back:"#f5e0e9",color:"#4c2337"},  
                 {back:"#e3e2e0",color:"#32302c"},  
                 {back:"#a8e6cf",color:"#3d3b4f"},    
                 {back:"#dfe7fd",color:"#1b263b"},  
                 {back:"#ffcfdf",color:"#5a001e"},  
            ];


function Today() {

    const dispatch = useDispatch()
    const {todaysTasks,userData} = useSelector((state)=> state.userSlice)
    const [showDoneTasks,setShowDoneTasks] = useState(false)
    const [task,setTask] = useState("")


    let lastIndex = 0
    function pickColor(){
        let index = Math.floor(Math.random() * 11);
        while(index === lastIndex){
            index = Math.floor(Math.random() * 11);
        }
        lastIndex = index
        return index
    }

    const handleAddTask = ()=>{
        const newTask = {task,status:false}
        dispatch(addTask({_id:userData._id,newTask})).then(setTask(""))
    }
    const handleDoneTask=(index)=>{
        dispatch(doneTask({_id:userData._id,index}))
    }

    
    const [selectedTaskIndex, setSelectedTaskIndex] = useState(null);

    function handleClick(event,index) {
        setSelectedTaskIndex(index)
    }

    useEffect(()=>{},[todaysTasks])

    return (
        <div className='Today'>

            <button className='flip-page' onClick={()=>setShowDoneTasks(!showDoneTasks)}>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                <path stroke-linecap="round" stroke-linejoin="round" d="M7.5 21L3 16.5m0 0L7.5 12M3 16.5h13.5m0-13.5L21 7.5m0 0L16.5 12M21 7.5H7.5" />
                </svg>
            </button>

            <div className='tasks-pending' style={{transform: showDoneTasks ? "translateX(-100%)" :"translateX(0%)"}}>
                <div className='tasks-pending-header'>
                    <h2>Today's Goals</h2>
                </div>  

                <div className='TASKS-container'>
                    {
                        todaysTasks.length > 0 
                        ? todaysTasks.map((todoTasks,index)=>{ 
                            if(!todoTasks.status){
                                const cindex = pickColor()
                                return  <div key={index} 
                                             className='TASKS' 
                                             style={{backgroundColor : color[cindex].back}}>
                                             <p onClick={(e) => handleClick(e,index)} 
                                                style={{color:color[cindex].color}}>{todoTasks.task}</p>
                                             {
                                                selectedTaskIndex === index && <ul onMouseLeave={()=>setSelectedTaskIndex(false)} className='tasks-menu'>
                                                    <li>
                                                        <button onClick={()=>handleDoneTask(index)}>
                                                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.2" stroke="currentColor" class="w-6 h-6">
                                                            <path stroke-linecap="round" stroke-linejoin="round" d="M9 12.75L11.25 15 15 9.75M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                                                            </svg>
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button onClick={()=>handleDoneTask(index)}>
                                                        <svg width="24" height="24" stroke-width="1" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M5.45887 2L1 6.01478L2.33826 7.50107L6.79713 3.48629L5.45887 2Z" fill="currentColor" /><path d="M11 8H13V12H16V14H11V8Z" fill="currentColor" /><path fill-rule="evenodd" clip-rule="evenodd" d="M3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12ZM5 12C5 8.13401 8.13401 5 12 5C15.866 5 19 8.13401 19 12C19 15.866 15.866 19 12 19C8.13401 19 5 15.866 5 12Z" fill="currentColor" /><path d="M18.5411 2L23 6.01478L21.6617 7.50107L17.2029 3.48629L18.5411 2Z" fill="currentColor" /></svg>
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button>
                                                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1" stroke="currentColor" class="w-6 h-6">
                                                            <path stroke-linecap="round" stroke-linejoin="round" d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0" />
                                                            </svg>
                                                        </button>
                                                    </li>
                                                </ul>
                                            }
                                        </div>
                            }})
                        :<h2>hehehhe</h2>
                    }
                </div>

                <div className='input-task'>
                    <input placeholder='Enter Task' type="text" onChange={(e)=>{setTask(e.target.value)}} value={task}></input>
                    <button onClick={()=>handleAddTask()}>
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M8.01562 6.98193C7.46334 6.98193 7.01562 7.43285 7.01562 7.98513C7.01562 8.53742 7.46334 8.98833 8.01563 8.98833H15.9659C16.5182 8.98833 16.9659 8.53742 16.9659 7.98513C16.9659 7.43285 16.5182 6.98193 15.9659 6.98193H8.01562Z" fill="currentColor" /><path d="M7.01562 12C7.01562 11.4477 7.46334 10.9968 8.01562 10.9968H15.9659C16.5182 10.9968 16.9659 11.4477 16.9659 12C16.9659 12.5523 16.5182 13.0032 15.9659 13.0032H8.01563C7.46334 13.0032 7.01562 12.5523 7.01562 12Z" fill="currentColor" /><path d="M8.0249 15.0122C7.47262 15.0122 7.0249 15.4631 7.0249 16.0154C7.0249 16.5677 7.47262 17.0186 8.0249 17.0186H15.9752C16.5275 17.0186 16.9752 16.5677 16.9752 16.0154C16.9752 15.4631 16.5275 15.0122 15.9752 15.0122H8.0249Z" fill="currentColor" /><path fill-rule="evenodd" clip-rule="evenodd" d="M3 6C3 4.34315 4.34315 3 6 3H18C19.6569 3 21 4.34315 21 6V18C21 19.6569 19.6569 21 18 21H6C4.34315 21 3 19.6569 3 18V6ZM6 5H18C18.5523 5 19 5.44772 19 6V18C19 18.5523 18.5523 19 18 19H6C5.44772 19 5 18.5523 5 18V6C5 5.44772 5.44772 5 6 5Z" fill="currentColor" /></svg>
                    </button>
                    <button onClick={()=>handleAddTask()}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.2" stroke="currentColor" class="w-6 h-6">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
                        </svg>
                    </button>
                </div>
            </div>  

            <div className='tasks-done' style={{transform: showDoneTasks ? "translateX(0%)" :"translateX(100%)"}}>
                <div className='tasks-pending-header'>
                    <h2>Today's Completed Goals</h2>
                </div> 
                <div className='TASKS-container'>
                        {
                            todaysTasks.length > 0 
                            ? todaysTasks.map((todoTasks,index)=>{ 
                                if(todoTasks.status){
                                    const cindex = pickColor()
                                    return  <div key={index} 
                                                className='TASKS' 
                                                style={{backgroundColor : color[cindex].back}}>
                                                <p style={{color:color[cindex].color}}>{todoTasks.task}</p>
                                            </div>
                                }})
                            :
                                todaysTasks.length === 0 ? 
                                <h4>You have not added any goals for today. Pleae Add some goals and try to complete them.</h4>
                                :
                                <h2>You have not Completed any goals today</h2>
                        }
                    </div>
            </div>

        </div>
    )
}

export default Today
