import React from 'react'
import "./Today.css"
import { useSelector } from 'react-redux'


function Today() {

    const {showTodayInMiddeSreen} = useSelector(state => state.uiSlice)

    const getTodayStyle = useMemo(() => {
        return (showTodayInMiddeSreen) => ({
          transform: showTodayInMiddeSreen ? 'translateX(-24%)' : 'translateX(0%)',
          width: showTodayInMiddeSreen ? '36%' : '30%'
        });
      }, []);
    
      const todayStyle = getTodayStyle(showTodayInMiddeSreen);
    
    return (
        <div className='Today' style={{
                                        transform : showTodayInMiddeSreen ? "translateX(-24%)" : "translateX(0%)",
                                        width : showTodayInMiddeSreen ? "36%": "30%"
        }}>Today</div>
    )
}

export default Today
