import React, { useState } from 'react'
import './Achievements.css'
import { useSelector } from 'react-redux'

function Achievements() {

    const monthDivs = []
    const {userData} = useSelector((state)=> state.userSlice)
    const {today} = useSelector((state)=> state.uiSlice)
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const [showDateInfo,setShowDateInfo] = useState(false)
    const [dateInfoData,setDateInfoData] = useState({})

    const year = today.year

    const handleMouseOver = (obj,e)=>{

        console.log(e,obj)
        const dateDiv = e.target.getBoundingClientRect()
        
        setDateInfoData({dateDiv,date:obj.j,month:obj.i+1})
        setShowDateInfo(true)

    }


    for (let i = 0; i <= 11; i++){

        const firstDayOfNextMonth = new Date(year, i + 1, 1);
        const lastDayOfMonth = new Date(firstDayOfNextMonth - 1);
        const days = lastDayOfMonth.getDate();

        let weekNumber = new Date(year, i, 1)
        weekNumber = weekNumber.getDay()
        //0 -> sunday       

        const currMonth = []
        let week = []
        let counter = 1

        for (let j = 1; j <= days; j++,counter++){

            if(counter <= 7){

                if(weekNumber > 0){
                    week.push(<div className='null day'></div>)
                    j -= 1
                    weekNumber-=1
                }
                else{
                    
                    if(userData.everyday[i][j]){
                        const efficiency = Number(userData.everyday[i][j].efficiency)

                        if(efficiency < 30){
                            week.push(<div key={j}style={{backgroundColor:"#F16767"}} className='day'></div>)
                        }
                        else if(efficiency <= 50){
                            week.push(<div key={j} onMouseOver={(e)=>handleMouseOver(e,{i,j})}
                            style={{backgroundColor:"#FFAE6D"}} className='day'></div>)
                        }
                        else if(efficiency <= 70){
                            week.push(<div key={j} style={{backgroundColor:"#68B984"}} className='day'></div>)
                        }
                        else{
                            week.push(<div key={j} style={{backgroundColor:"#3D8361"}} className='day'></div>)
                        }

                    }
                    else{
                        week.push(<div key={j} className='day'></div>)
                    }
                }
            }
            else{
                currMonth.push(<div className='week'>{week}</div>)
                j -= 1
                week = []
                counter = 0
            }
        }

        while(counter <= 7){
            week.push(<div className='null day'></div>)
            counter += 1
        }

        currMonth.push(<div className='week'>{week}</div>)

        monthDivs.push(<div className='month'><div className='weeks'>{currMonth}</div><p>{months[i]}</p></div>)

    }

    return (
        <div className='Achievements'>
            <h3>Achievements</h3>
            <div className='year-dashboard'>
                { showDateInfo && <div className='day-info'>heheheh</div>}
                {
                    monthDivs
                }
            </div>
        </div>
    )
}

export default Achievements
