import React, { useRef, useState } from 'react'
import "./Today.css"
import history from  '../../Assets/history.png'
import {useDispatch, useSelector} from 'react-redux'
import { addTask } from '../../Redux/userSlice'
import e from 'cors'


const color = [  {back:"#e8deee",color:"#412454"},  
                 {back:"#eee0da",color:"#442a1e"},  
                 {back:"#d3e5ef",color:"#183347"},  
                 {back:"#fdecc8",color:"#402c1b"},  
                 {back:"#ffe2dd",color:"#5d1715"},  
                 {back:"#dbeddb",color:"#1c3829"},  
                 {back:"#f5e0e9",color:"#4c2337"},  
                 {back:"#e3e2e0",color:"#32302c"},  
                 {back:"#a8e6cf",color:"#3d3b4f"},    
                 {back:"#dfe7fd",color:"#1b263b"},  
                 {back:"#ffcfdf",color:"#5a001e"},  
            ];


function Today() {

    const dispatch = useDispatch()
    const {todaysTasks,todaysDoneTasks,userData} = useSelector((state)=> state.userSlice)
    const [showDoneTasks,setShowDoneTasks] = useState(false)
    const [task,setTask] = useState("")


    let lastIndex = 0
    function pickColor(){
        let index = Math.floor(Math.random() * 11);
        while(index === lastIndex){
            index = Math.floor(Math.random() * 11);
        }
        lastIndex = index
        return index
    }

    const handleAddTask = ()=>{
        const newTask = {task,status:false}
        dispatch(addTask({_id:userData._id,newTask})).then(setTask(""))
    }


    const menu = useRef(null);
    const [showMenu, setShowMenu] = useState(false);
    const [menuPos, setMenuPos] = useState({ x: 0, y: 0 });

    const handleShowMenu = (e) => {
        e.preventDefault();
        setShowMenu(true);
        const rect = e.target.getBoundingClientRect();
        setMenuPos({ x: rect.left + window.pageXOffset, y: rect.top + window.pageYOffset });
    };
      

    const TASKS = {
        display: "inline-block",
        fontSize: "1rem",
        padding: ".6rem",
        border: "2px",
        scale: "0.98",
        transition: "all 0.2s",
        cursor: "pointer",
    }

    return (
        <div className='Today'>

            <button className='flip-page' onClick={()=>setShowDoneTasks(!showDoneTasks)}>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                <path stroke-linecap="round" stroke-linejoin="round" d="M7.5 21L3 16.5m0 0L7.5 12M3 16.5h13.5m0-13.5L21 7.5m0 0L16.5 12M21 7.5H7.5" />
                </svg>
            </button>


            <div className='tasks-pending' style={{transform: showDoneTasks ? "translateX(-100%)" :"translateX(0%)"}}>
                <div className='tasks-pending-header'>
                    <h2>Today's Tasks</h2>
                    <button><img src={history}></img></button>
                </div>  

                <div className='TASKS-container'>
                    {
                        todaysTasks.length > 0 
                        ? todaysTasks.map((todoTasks,index)=>{ 
                            if(!todoTasks.status){
                                const cindex = pickColor()
                                return  <div onClick={handleShowMenu} key={index} className='TASKS' style={{backgroundColor : color[cindex].back}}>
                                            <button style={{color : color[cindex].color}}>{todoTasks.task}</button>
                                        </div>
                        }})
                        :<h2>hehehhe</h2>
                    }
                </div>

                {
                    showMenu && <ul ref={menu} className='right-click-menu' style={{ top: menuPos.y, left: menuPos.x, position: 'absolute' }}
                    >
                        <li><button>Option 1</button></li>
                        <li><button>Option 2</button></li>
                        <li><button>LogOut</button></li>
                    </ul>
                }

                <div className='input-task'>
                    <input placeholder='Enter Task' type="text" onChange={(e)=>{setTask(e.target.value)}} value={task}></input>
                    <button onClick={()=>handleAddTask()}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.2" stroke="currentColor" class="w-6 h-6">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
                        </svg>
                    </button>
                </div>
            </div>  

            {/* top: 279px;
            left: 160px; */}


            <div className='tasks-done' style={{transform: showDoneTasks ? "translateX(0%)" :"translateX(100%)"}}>
                <h2>Done Tasks</h2>
            </div>

        </div>
    )
}

export default Today
